import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { FaGithub, FaLinkedin, FaInstagram } from "react-icons/fa";
import { HiOutlineMail } from "react-icons/hi";
import SpotlightCard from "./components/SpotlightCard";
import TextType from "./components/TextType";
import Spotlight from "./components/Spotlight";


export default function Portfolio() {
  const [activeSection, setActiveSection] = useState("");

  const sections = [
    { id: "about", label: "About" },
    { id: "experience", label: "Experience" },
    { id: "projects", label: "Projects" },

    { id: "contact", label: "Contact" }
  ];

  useEffect(() => {
    document.documentElement.classList.add("dark");
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setActiveSection(entry.target.id);
          }
        });
      },
      {
        rootMargin: "-50% 0px -40% 0px",
        threshold: 0,
      }
    );

    sections.forEach((section) => {
      const el = document.getElementById(section.id);
      if (el) observer.observe(el);
    });

    return () => observer.disconnect();
  }, []);

  return (
  <div className="relative w-full h-screen overflow-hidden bg-[#0c0c0f] text-slate-300 font-inter">
    <Spotlight />

    <div className="relative z-10 w-full max-w-6xl mx-auto flex h-screen">
      {/* Left Column - Hero & Nav */}
      <aside className="w-full md:w-[30%] p-8 pt-24 sticky top-0 h-screen flex flex-col justify-between">
        <div>
          <h1 className="text-4xl font-bold text-white mb-2">Abijith Subramaniyan Kunnath</h1>
          <h2 className="text-xl text-teal-400 mb-4 flex items-center gap-1">
            <span className="text-teal-400">Data</span>
            <TextType
              text={[
                "Science Graduate",
                "Analyst?",
                "Engineer?",
                "Specialist?",
              ]}
              typingSpeed={60}
              deletingSpeed={30}
              pauseDuration={2000}
              initialDelay={500}
              loop={true}
              showCursor={true}
              hideCursorWhileTyping={false}
              cursorCharacter="_"
              cursorClassName="text-white"
              cursorBlinkDuration={0.5}
              className="text-teal-400"
            />
          </h2>

          <p className="text-slate-400 leading-relaxed">
            I build data-powered digital experiences for web and mobile. Passionate about ML, analytics, and elegant software solutions.
          </p>
        </div>

        <nav className="mt-8 space-y-3 font-mono text-sm">
          {sections.map((section) => (
            <a
              key={section.id}
              href={`#${section.id}`}
              className={`block font-mono text-sm transition ${
                activeSection === section.id
                  ? "text-white font-bold"
                  : "text-teal-400 hover:text-white"
              }`}
            >
              {section.label}
            </a>
          ))}
        </nav>

        <div className="mt-10 space-y-4">
          <div className="flex space-x-4 text-2xl text-slate-400">
            <a href="https://github.com/AbijithSubramaniyan" target="_blank" rel="noopener noreferrer" className="hover:text-teal-400">
              <FaGithub />
            </a>
            <a href="https://linkedin.com/in/abijith-subramaniyan-kunnath-851567126" target="_blank" rel="noopener noreferrer" className="hover:text-teal-400">
              <FaLinkedin />
            </a>
            <a href="https://instagram.com/abijithsubramaniyan" target="_blank" rel="noopener noreferrer" className="hover:text-teal-400">
              <FaInstagram />
            </a>
            <a href="mailto:abijith.subramaniyan@gmail.com" className="hover:text-teal-400">
              <HiOutlineMail />
            </a>
          </div>
        </div>
      </aside>

      {/* Right Column - Scrollable Sections */}
      <main className="w-full md:w-[70%] pt-24 overflow-y-auto h-screen pr-2">
        {sections.map((section, index) => (
          <motion.section
            key={section.id}
            id={section.id}
            className="max-w-2xl w-full mx-auto py-16 px-6"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, amount: 0.2 }}
            transition={{ duration: 0.6, delay: index * 0.05 }}
          >
            {section.id === "about" && (
              <p className="text-lg leading-relaxed">
                Motivated and adaptable Data Science postgraduate...
              </p>
            )}

            {section.id === "experience" && (
              <div className="space-y-6">
                <SpotlightCard>...</SpotlightCard>
                <SpotlightCard>...</SpotlightCard>
              </div>
            )}

            {section.id === "projects" && (
              <div className="flex flex-col gap-6">
                <SpotlightCard>...</SpotlightCard>
                <SpotlightCard>...</SpotlightCard>
              </div>
            )}

            {section.id === "contact" && (
              <div>
                <p>Email: abijith.subramaniyan@gmail.com</p>
                <p>Phone: 0430019454</p>
                <p>LinkedIn: <a href="https://linkedin.com/in/abijith-subramaniyan-kunnath-851567126" className="text-teal-400 underline">My LinkedIn</a></p>
              </div>
            )}
          </motion.section>
        ))}
      </main>
    </div>
  </div>
);

}
