import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function Portfolio() {
  const [darkMode, setDarkMode] = useState(true);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  return (
    <div className="font-sans bg-[#0a192f] text-slate-300 dark:bg-[#0a192f] dark:text-slate-300">
      {/* Dark Mode Toggle */}
      <div className="fixed top-4 right-4 z-50">
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="bg-slate-800 text-white px-4 py-2 rounded-lg text-sm border border-slate-600 hover:bg-slate-700 dark:bg-slate-200 dark:text-slate-900 dark:hover:bg-slate-100"
        >
          {darkMode ? "Light Mode" : "Dark Mode"}
        </button>
      </div>

      <section className="min-h-screen flex flex-col justify-center items-center text-center p-8">
        <motion.h1
          className="text-4xl md:text-6xl font-bold mb-4 text-white"
          initial={{ opacity: 0, y: -30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          Abijith Subramaniyan Kunnath
        </motion.h1>

        <motion.p
          className="text-xl md:text-2xl mb-6 text-teal-400"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4, duration: 0.8 }}
        >
          Data Science Graduate | SAP & ML Enthusiast | Android App Developer
        </motion.p>

        <motion.div
          className="space-x-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.8 }}
        >
          <a href="/Abijith_Subramaniyan_Kunnath_Resume.pdf" download className="bg-transparent text-teal-400 border border-teal-400 px-6 py-2 rounded-xl font-semibold hover:bg-teal-500 hover:text-white transition">View Resume</a>
          <a href="#projects" className="bg-transparent text-teal-400 border border-teal-400 px-6 py-2 rounded-xl font-semibold hover:bg-teal-500 hover:text-white transition">Projects</a>
        </motion.div>
      </section>

      {/* Remaining sections will be styled in upcoming steps */}
    </div>
  );
}
